printf "\e[${COLUMN1_BACKGROUND};${COLUMN1_FONT_COLOR}mRAM_USED\e[${RESET}m  = \e[${COLUMN2_BACKGROUND};${COLUMN2_FONT_COLOR}m$RAM_USED\e[${RESET}m\n"
