printf "\e[${COLUMN1_BACKGROUND};${COLUMN1_FONT_COLOR}mSPACE_ROOT_USED\e[${RESET}m  = \e[${COLUMN2_BACKGROUND};${COLUMN2_FONT_COLOR}m$SPACE_ROOT_USED\e[${RESET}m\n"
printf "\e[${COLUMN1_BACKGROUND};${COLUMN1_FONT_COLOR}mSPACE_ROOT_FREE\e[${RESET}m  = \e[${COLUMN2_BACKGROUND};${COLUMN2_FONT_COLOR}m$SPACE_ROOT_FREE\e[${RESET}m\n"

